import React, { useState } from "react";
import Comment from "./Comment";

const Video = () => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  const handleCommentSubmit = (event) => {
    event.preventDefault();
    if (newComment.trim() !== "") {
      setComments([
        ...comments,
        { text: newComment, upvotes: 0, downvotes: 0 }
      ]);
      setNewComment("");
    }
  };

  const handleUpvote = (index) => {
    setComments((prevComments) => {
      const updatedComments = [...prevComments];
      updatedComments[index].upvotes += 1;
      return updatedComments;
    });
  };

  const handleDownvote = (index) => {
    setComments((prevComments) => {
      const updatedComments = [...prevComments];
      updatedComments[index].downvotes += 1;
      return updatedComments;
    });
  };

  return (
    <div className="App">
      <video width="750" height="500" controls>
        <source src="Liikakalastus.mp4" type="video/mp4" />
      </video>
      <div>
        <form onSubmit={handleCommentSubmit}>
          <textarea
            value={newComment}
            onChange={(event) => setNewComment(event.target.value)}
            placeholder="Write a comment..."
          />
          <button type="submit">Submit</button>
        </form>
        {comments.map((comment, index) => (
          <Comment
            key={index}
            text={comment.text}
            upvotes={() => handleUpvote(index)}
            downvotes={() => handleDownvote(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default Video;
